/**
 * Gets a cookie.
 *
 * @param  {String} cookieName The cookie name.
 * @return {String|null}       Cookie value, else \`null\`.
 */
function lpGetCookie( cookieName ) {
if ( ! cookieName ) {
return null;
}
return decodeURIComponent( document.cookie.replace( new RegExp( "(?:(?:^|.*;)\\\\s*" + encodeURIComponent( cookieName ).replace( /[\\-\\.\\+\\*]/g, "\\\\$&" ) + "\\\\s*\\\\=\\\\s*([^;]*).*$)|^.*$" ), "$1" ) ) || null;
}

/**
 * Set a cookie.
 * Overwrites one if it already exists.
 *
 * @param {String} cookieName - The cookie name.
 * @param {String} cookieValue - The cookie value.
 * @param {Number} expirationInDays - The number of days until expiration, defaults to 30.
 */
function lpSetCookie( cookieName, cookieValue, expirationInDays ) {
var dateObject, expiration;

// Default to 30 days if no expiration is given
expirationInDays = expirationInDays || 30;

// Calculate the expiration in milliseconds
dateObject = new Date();
dateObject.setTime( dateObject.getTime() + ( expirationInDays * 24 * 60 * 60 * 1000 ) );
expiration = 'expires=' + dateObject.toUTCString();

// Set the cookie the same for all subdomains (.wordpress.com) and for the / path
document.cookie = cookieName + '=' + cookieValue + '; ' + expiration + '; domain=.wordpress.com; path=/';
}

/**
 * Returns the product monthly price (formatted according to locale) including currency symbol.
 * Example:
 *
 * @param {Integer} rawPrice The price of the plan found in ./currency-prices.js
 * @param {String} currency A currency code.
 * @param {String} currencySymbol A currency symbol (used only as fallback when Intl.NumberFormat not available).
 * @returns {String} Monthly price formatted according to locale, including currency symbol.
 */
function getFormattedMonthlyPrice( rawPrice, currency, currencySymbol ) {
// Apply monthly prices
var monthlyValue = rawPrice / 12;

// JPY is always an integer number
var numberOfDecimals = ( 0 === monthlyValue || 'JPY' === currency ) ? 0 : 2;

monthlyValue = monthlyValue.toFixed( numberOfDecimals );

var monthlyValueInt = parseInt( monthlyValue );

var monthlyDecimalValueSplit = monthlyValue.toString().split( '.' );

var monthlyDecimalValue = monthlyDecimalValueSplit[ 1 ] ? '.' + monthlyDecimalValueSplit[ 1 ] : '';

var monthlyDecimalRounded = ( '.00' === monthlyDecimalValue ) ? '' : monthlyDecimalValue;

// Remove decimals if the price is a round number, or numberOfDecimals is set to zero.
var minimumFractionDigits = ( 0 === numberOfDecimals || '.00' === monthlyDecimalValue ) ? 0 : 2;

// See: https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/NumberFormat
if ( window.Intl && 'function' === typeof Intl.NumberFormat ) {
return new Intl.NumberFormat( 'en', { style: 'currency', currency: currency, minimumFractionDigits: minimumFractionDigits  } ).format( monthlyValue );
}

return currencySymbol + monthlyValueInt.toString().replace( /\\B(?=(\\d{3})+(?!\\d))/g, ',' ) + monthlyDecimalRounded;
}

/**
* Change currency
*/
function changeCurrency( iso ) {
if ( ! iso || ! currencies.hasOwnProperty( iso ) ) {
iso = "USD";
}

for ( var i in currencies[iso].prices ) {
var plan_value_element = jQuery ( '.' + i );

if ( ! plan_value_element.length ) {
continue;
}

var origPrice = currencies[iso].prices[ 'orig-' + i ] || 0,
origPriceMarkup = origPrice ? '<del>' + getFormattedMonthlyPrice( origPrice, iso, currencies[iso].symbol ) + '</del> ': '';

plan_value_element.html( origPriceMarkup + '<sup>' + getFormattedMonthlyPrice( currencies[iso].prices[i], iso, currencies[iso].symbol ) + '</sup>' );
}

currencyISO = iso; // Change currency.
lpSetCookie( 'landingpage_currency', iso, 365 );
}

